func IntToBytes(n int) []byte {
	// ÂàõÂª∫‰∏Ä‰∏™Â≠óËäÇÁºìÂÜ≤Âå∫
	byteBuffer := new(bytes.Buffer)

	// Â∞ÜÊï¥ÂûãËΩ¨Êç¢‰∏∫int64Á±ªÂûãÔºå‰ª•ÈÄÇÂ∫î‰∏çÂêåÂ§ßÂ∞èÁöÑÊï¥Âûã
	var num int64 = int64(n)

	// ‰ΩøÁî®binary.WriteÂ∞Üint64Á±ªÂûãÁöÑÊï∞ÊçÆ‰ª•‰∫åËøõÂà∂ÂΩ¢ÂºèÂÜôÂÖ•Â≠óËäÇÁºìÂÜ≤Âå∫
	err := binary.Write(byteBuffer, binary.BigEndian, num)
	if err != nil {
		fmt.Println("binary.Write failed:", err)
		return nil
	}

	// ËøîÂõûÂ≠óËäÇÁºìÂÜ≤Âå∫ÁöÑÂ≠óËäÇÊï∞ÁªÑ
	return byteBuffer.Bytes()
}

func int32ToBytes(i int32) []byte {
	bytes := make([]byte, 4)
	binary.LittleEndian.PutUint32(bytes, uint32(i))
	return bytes
}

func int64ToBytes(i int64) []byte {
	bytes := make([]byte, 8)
	binary.LittleEndian.PutUint64(bytes, uint64(i))
	return bytes
}
func IntToBytesBigEndian(n int64, bytesLength byte) ([]byte, error) {
	switch bytesLength {
	case 1:
		tmp := int8(n)
		bytesBuffer := bytes.NewBuffer([]byte{})
		binary.Write(bytesBuffer, binary.BigEndian, &tmp)
		return bytesBuffer.Bytes(), nil
	case 2:
		tmp := int16(n)
		bytesBuffer := bytes.NewBuffer([]byte{})
		binary.Write(bytesBuffer, binary.BigEndian, &tmp)
		return bytesBuffer.Bytes(), nil
	case 3:
		tmp := int32(n)
		bytesBuffer := bytes.NewBuffer([]byte{})
		binary.Write(bytesBuffer, binary.BigEndian, &tmp)
		return bytesBuffer.Bytes()[1:], nil
	case 4:
		tmp := int32(n)
		bytesBuffer := bytes.NewBuffer([]byte{})
		binary.Write(bytesBuffer, binary.BigEndian, &tmp)
		return bytesBuffer.Bytes(), nil
	case 5:
		tmp := n
		bytesBuffer := bytes.NewBuffer([]byte{})
		binary.Write(bytesBuffer, binary.BigEndian, &tmp)
		return bytesBuffer.Bytes()[3:], nil
	case 6:
		tmp := n
		bytesBuffer := bytes.NewBuffer([]byte{})
		binary.Write(bytesBuffer, binary.BigEndian, &tmp)
		return bytesBuffer.Bytes()[2:], nil
	case 7:
		tmp := n
		bytesBuffer := bytes.NewBuffer([]byte{})
		binary.Write(bytesBuffer, binary.BigEndian, &tmp)
		return bytesBuffer.Bytes()[1:], nil
	case 8:
		tmp := n
		bytesBuffer := bytes.NewBuffer([]byte{})
		binary.Write(bytesBuffer, binary.BigEndian, &tmp)
		return bytesBuffer.Bytes(), nil
	}
	return nil, fmt.Errorf("IntToBytesBigEndian b param is invaild")
}

